import React, { useEffect, useState, Ref, useRef, useContext } from 'react'
import Cookies from "js-cookie";
import {DeleteContext} from '../context/DeleteContext'


const DeleteMessageComponent = (props) =>{
    const {controller} = useContext(DeleteContext)
    console.log(controller)

    const [controlModal, setControlModal] = useState(props.cond)

useEffect(()=>{
    setControlModal(props.cond)
})


const closeModal = () =>{
    setControlModal('closeDelete')
}
    const deleteFunc = () => {
        fetch(`http://localhost:5000/delete-messages/${props.selected}`,{
            method: 'DELETE',
            mode: 'cors',
            headers: {
			"Content-Type": "application/x-www-form-urlencoded",
			"Access-Control-Allow-Origin": "*",
			"x-access-token": Cookies.get("token"),
            }
        }).then((response) => response.json())
        .then((resp) => {
            window.location.reload()
        })
    }

    return(
        <>
        <div className={`deleteMessageCard ${controlModal}`}>
            <div className="deleteCard">
            <h3 style={{marginTop: '0px'}}>Are you sure?</h3>
            {/* <button onClick={cancelClick} id='submit-btn-update'>Cancel</button> */}
            <button onClick={deleteFunc} id='submit-btn-update'>Delete</button>
            <button onClick={closeModal} id='submit-btn-update'>Cancel</button>
            </div>


        </div>
        </>
    )
}
export default DeleteMessageComponent;
