import React, { useEffect, useState, Ref, useRef } from 'react'
import Footer from './components/Footer'
import Cookies from "js-cookie";
import CreateMsgbutton from './components/Createmsgbutton';
import DeleteMessageComponent from './components/DeleteMessageComponent';

const Message = (props) => {
	const [messageItem, setMessageItem] = useState({
		items: null,
		loaded: false,
    });
	const [selected, setSelected] = useState(props.selected);
	const [selectID, setSelectID] = useState({
		id: 0,
		loaded: false
	})
	const [message, setMessage] = useState({
		message: '',
		url: '',
		loaded: false
	})
	const [deleteOnClick, setDeleteOnClick] = useState(false)

	useEffect(() => {

		fetch("http://localhost:5000/onload-messages", {
			method: "GET",
			mode: "cors",
			headers: {
				"Content-Type": "application/x-www-form-urlencoded",
				"Access-Control-Allow-Origin": "*",
				"x-access-token": Cookies.get("token"),
			},
		})
			.then((resp) => resp.json())
			.then((items) => {
				setMessageItem({ items: Object.values(items), loaded: true });
				// Call another fetch to return an obj
				setSelectID({id: Object.values(items)[0].id, loaded: true})

				return fetch(`http://localhost:5000/template/${Object.values(items)[0].id}`,{
				method: "GET",
				mode: "cors",
				headers: {
					"Content-Type": "application/x-www-form-urlencoded",
					"Access-Control-Allow-Origin": "*",
					"x-access-token": Cookies.get("token"),
				}
				})
			}).then((response) => response.json())
			.then((resp_items) =>{
				setMessage({message: Object.values(resp_items)[0].Message, url: Object.values(resp_items)[0].url, loaded: true})
				console.log(Object.values(resp_items)[0].Message)
			})




	}, []);




// TESTING
const selectedTemplate = e => {
	const selection_id = e.target.value

setSelectID({id: selection_id, loaded: true})

fetch(`http://localhost:5000/template/${selection_id}`,{
				method: "GET",
				mode: "cors",
				headers: {
					"Content-Type": "application/x-www-form-urlencoded",
					"Access-Control-Allow-Origin": "*",
					"x-access-token": Cookies.get("token"),
				}
				}).then((response) => response.json())
			.then((resp_items) =>{
				setMessage({message: Object.values(resp_items)[0].Message, url: Object.values(resp_items)[0].url, loaded: true})
			})
}

// Deleting selection
const deleteSelected = () => {
	setDeleteOnClick(!deleteOnClick)
	// fetch(`http://localhost:5000/delete-messages/${selection_id}`,{

}


//  Render load conditions
    var {loaded } = messageItem;

	if (!loaded) {
		return (
			<>
				<p>Loading...</p>
			</>
		);
	} else {

    return(
        <>
        <div className='app-bg'>
            {/* Card  */ console.log(selectID.id)}
            <div className='cardHolder'>
            <div className='message_card'>
				<div className="image_holder">
					<img className="displayImg" src={selectID.loaded ? `http://localhost:5000/images/${selectID.id}`: `http://localhost:5000/images/4`} alt='content_image'/>
					<p style={{
						textAlign: 'left',
						paddingLeft: '10px',
						marginBottom: '2px'}}>{message.loaded ? message.message : 'No message found'}</p>
					<p style={{
					textAlign: 'left',
					paddingLeft: '10px',
					color: 'blue',
					textDecoration: 'underline',
					marginTop: '1px'}}>{message.loaded ? message.url : 'No url found'}</p>
				</div>
            </div>
            </div>

        <h2 style={{textAlign: 'left', marginLeft: '5%'}}>Select Template</h2>
        <select id='selectId' onChange={selectedTemplate}>
            {messageItem.items.map((obj)=>(
                <option style={{color:'#6B6B6B', display: 'flex', flexDirection: 'column'}} key={obj.id} value={obj.id}>{obj.title}</option>
                ))}
        </select>

		{/* Delete message component */}
		<div style={deleteOnClick ? {visibility: 'visible'} : {visibility: 'hidden'}}>
		<DeleteMessageComponent selected={selectID.id} cond={deleteOnClick}/>
		</div>


		<button onClick={deleteSelected}>{deleteOnClick ? 'Cancel' : 'Delete'}</button>
        <div className='msgBtn'>
        <CreateMsgbutton />
        </div>
       <Footer />
        </div>
        </>
    )}
}

export default Message;
