import React, { useEffect, useState, Ref, useRef } from 'react'
import Cookies from "js-cookie";


const DeleteMessageComponent = (props) =>{
    console.log(props.cond)
const [cancel, setCancel] = useState({
    visibility: ''
})
    const cancelClick = () => {
        setCancel(!cancel)
        console.log(cancel)
    }
    const deleteFunc = () => {
        console.log(props.selected)
        fetch(`http://localhost:5000/delete-messages/${props.selected}`,{
            method: 'DELETE',
            mode: 'cors',
            headers: {
			"Content-Type": "application/x-www-form-urlencoded",
			"Access-Control-Allow-Origin": "*",
			"x-access-token": Cookies.get("token"),
            }
        }).then((response) => response.json())
        .then((resp) => {
            console.log(resp)
            window.location.reload()
        })
    }

    return(
        <>
        <div style={props.cond ? {visibility: 'visible'} : {visibility: 'hidden'}} className="deleteMessageCard">
            <div className="deleteCard">
            <h1> Are you sure?</h1>
            <button onClick={cancelClick} id='submit-btn-update'>Cancel</button>
            <button onClick={deleteFunc} id='submit-btn-update'>Delete</button>
            </div>


        </div>
        </>
    )
}
export default DeleteMessageComponent;
