import { createContext, useReducer } from "react";
import {DeleteReducer} from '../Reducers/DeleteReducer'


export const DeleteContext = createContext()

const DeleteProvider = (props) =>{
// need to create reducer
// set inital state for the reducer
const init = [{controller: 'closeDelete'}]
const [controller, dispatch] = useReducer(DeleteReducer, init)


    return(
// create context provider also add props to Provider
<DeleteContext.Provider value={{controller, dispatch}}>
    {props.children}
</DeleteContext.Provider>
    )
}

export default DeleteProvider;
