import React, { useEffect, useState, useRef } from "react";
import Cookies from "js-cookie";
import CreateMsgbutton from "./Createmsgbutton";
import { useForm } from "react-hook-form";

const CreateMsgForm = () => {
	const { register, handleSubmit } = useForm();
	var myHeaders = new Headers();
	myHeaders.append("x-access-token", Cookies.get("token"));
	myHeaders.append("Content-Type", "multipart/form-data");
	myHeaders.append("Access-Control-Allow-Origin", "*");

	var formdata = new FormData();
	formdata.append("nf", "fd" )
	console.log(formdata)

	var requestOptions = {
		method: 'POST',
		headers: myHeaders,
		body: formdata,
		mode: 'cors',
		// redirect: 'follow'
	  };

	const onSubmit = (e) => {
		e.preventDefault()
		  fetch("http://127.0.0.1:5000/create-message", requestOptions)
		  .then(response => response.text())
		  .then(result => console.log(result))
		  .catch(error => console.log('error', error));
	};

	return (
		<>
			{/* <div  className='bg_blur'></div> */}
			<div className='form_modal_wrapper'>
				<div>
					{
						<h2 style={{ textAlign: "left", color: "#34A1AD" }}>
							{" "}
							Create New Template{" "}
						</h2>
					}
					{/*  */}

					<form name='nf' onSubmit={onSubmit}>
						{/* <input ref={register} name='File' id='file' type='file' /> */}
						<input name='nf' type='text' />
						<input type='submit' value='submit' />
					</form>
				</div>
				{/* <input id='close-btn' value='close' type='button'></input> */}
			</div>
		</>
	);
};

export default CreateMsgForm;

