import React, { useEffect, useState } from "react";
import Footer from "./components/Footer";
import Cookies from "js-cookie";
import Createbutton from "./components/Createbutton";

const Contacts = () => {
	const [contactItem, setContactItem] = useState({
		items: null,
		loaded: false,
	});
	const [Item, setItem] = useState([]);
	const [query, setQuery] = useState();

	useEffect(() => {
		// setCookies(Cookies.get('token'))
		console.log(Cookies.get("token"));
		fetch("http://localhost:5000/read-contacts", {
			method: "GET",
			mode: "cors",
			headers: {
				"Content-Type": "application/x-www-form-urlencoded",
				"Access-Control-Allow-Origin": "*",
				"x-access-token": Cookies.get("token"),
			},
		})
			.then((resp) => resp.json())
			.then((items) => {
				setContactItem({ items: Object.values(items), loaded: true });
			});
	}, [query]);

	console.log(contactItem);
	var { items, loaded } = contactItem;
	function clicks(id) {
		alert("id: " + id + " was clicked");
	}

	if (!loaded) {
		return (
			<>
				<p>Loading...</p>
			</>
		);
	} else {
		return (
			<div className='app-bg'>
				<h3 className='title'>Contacts</h3>
				<ul className='scrollDiv'>
					{contactItem.items.map((obj) => (
						<li style={{color:'#6B6B6B', display: 'flex'}}key={obj.id} onClick={() => clicks(obj.id)}>
							<b>{obj.name}</b>
                            | {obj.number}

						</li>
					))}
				</ul>
                <div id='create-btn-wrapper'>
                <Createbutton />
                </div>

				<Footer />
			</div>
		);
	}
};

export default Contacts;
