import React, { useEffect, useState, useContext } from "react";
import Footer from "../components/Footer";
import Cookies from "js-cookie";
import Createbutton from "../components/Createbutton";
import PopupModal from "../components/Popup-box";
import { ContactContext } from '../context/ContactsCtxt';


const Contacts = ({mcontacts}) => {
const  {dispatch } = useContext(ContactContext);
console.log(mcontacts.items)



	const [contacts, setContactItem] = useState({
		items: null,
		loaded: false,
	});

    const [popup, setPopup] = useState(false);
    const [selectedClient, setSelectedClient] = useState({
        id: '',
        name: '',
        number: ''
    })
    const [query, setQuery] = useState();

	useEffect(() => {
		// setCookies(Cookies.get('token'))
		console.log(Cookies.get("token"));
		fetch("http://localhost:5000/read-contacts", {
			method: "GET",
			mode: "cors",
			headers: {
				"Content-Type": "application/x-www-form-urlencoded",
				"Access-Control-Allow-Origin": "*",
				"x-access-token": Cookies.get("token"),
			},
		})
			.then((resp) => resp.json())
			.then((items) => {
				// setContactItem({ items: Object.values(items), loaded: true })
				dispatch({type: 'ADD_CONTACTS', contacts: {items: Object.values(items), loaded: true}})
			});
	}, [query]);



    var { items, loaded } = contacts;

	function popupModal(id, name, num) {
    setPopup(!popup)
    setSelectedClient({
        id: id,
        name: name,
        number: num
    })

	}

	if (!loaded) {
		return (
			<>
				<p>Loading...</p>
			</>
		);
	} else {
		return (
			<div className='app-bg'>
				<h3 className='title'>Contacts</h3>
				<ul className='scrollDiv'>
					{contacts.items.map((obj) => (
						<li style={{color:'#6B6B6B', display: 'flex', flexDirection: 'column'}} key={obj.id} onClick={() => popupModal(obj.id, obj.name, obj.number)}>
							<div style={{color:'black'}}><b>{obj.name}</b></div>
                            {obj.number}
                            {console.log(obj)}

						</li>
					))}
				</ul>
                <div id='create-btn-wrapper'>
                {popup?<PopupModal id={selectedClient.id} name={selectedClient.name} num={selectedClient.number} />:null}
                <Createbutton/>
                </div>

				<Footer />
			</div>
		);
	}
};

export default Contacts;
