import React, { useEffect, useState, Ref, useRef } from 'react'
import Footer from './components/Footer'
import Cookies from "js-cookie";
import CreateMsgbutton from './components/Createmsgbutton';

const Message = (props) => {
	const [messageItem, setMessageItem] = useState({
		items: null,
		loaded: false,
    });
	const [selected, setSelected] = useState(props.selected);
	useEffect(() => {

		fetch("http://localhost:5000/onload-messages", {
			method: "GET",
			mode: "cors",
			headers: {
				"Content-Type": "application/x-www-form-urlencoded",
				"Access-Control-Allow-Origin": "*",
				"x-access-token": Cookies.get("token"),
			},
		})
			.then((resp) => resp.json())
			.then((items) => {
				setMessageItem({ items: Object.values(items), loaded: true });
				// Call another fetch to return an obj
				console.log(Object.values(items)[0].id)
				return fetch(`http://localhost:5000/images/${Object.values(items)[0].id}`,{
				method: "GET",
				mode: "cors",
				headers: {
					"Content-Type": "application/x-www-form-urlencoded",
					"Access-Control-Allow-Origin": "*",
					"x-access-token": Cookies.get("token"),
				}
				})
			}).then((response) => response.json())
			.then((resp_items) =>{
				console.log(resp_items + " New Response")
			})




	}, []);




// TESTING
const changeTest = e => {
console.log(e.target.value)
}
    var {loaded } = messageItem;

	if (!loaded) {
		return (
			<>
				<p>Loading...</p>
			</>
		);
	} else {

    return(
        <>
        <div className='app-bg'>
            {/* Card  */}
            <div className='cardHolder'>
            <div className='message_card'>
            </div>
            </div>

        <h2 style={{textAlign: 'left', marginLeft: '5%'}}>Select Template</h2>
        <select id='selectId' onChange={changeTest}>
            {messageItem.items.map((obj)=>(
                <option style={{color:'#6B6B6B', display: 'flex', flexDirection: 'column'}} key={obj.id} value={obj.id}>{obj.title}</option>
                ))}
        </select>
        <div className='msgBtn'>
        <CreateMsgbutton />
        </div>
       <Footer />
        </div>
        </>
    )}
}

export default Message;
