import React, { useEffect, useState } from "react";

const FormModal = (props) => {
const [switchModal, setswitchModal] = useState()
useEffect(()=>{
    if(switchModal === 'none'){
        setswitchModal(props.switchModal)
    }

}, [])

console.log('switch; '+ switchModal)

const closeBlock = () => {
    if(switchModal === 'block'){
        setswitchModal('none')
        console.log(switchModal)
    }
}

	return (
        <>
        <div style={{display: switchModal}} className='bg_blur'></div>

		<div style={{display:switchModal}} className='form_modal_wrapper'>
			<form  id='form-data'>
				<label id='email'>Name</label>
				<input
					className='cus-input'
					type='text'
					name='name'
					placeholder='Name'
					// onChange={(e) =>
					// 	setUser({ ...user, email: e.target.value })}>
				></input>
				<label id='email'>Number</label>
				<input
					className='cus-input'
					name='user'
					type='text'
					placeholder='647 123 4567'
					// onChange={(e) =>
					// 	setUser({ ...user, password: e.target.value })}
				></input>
				<input id='submit-btn' type='submit' value='Create Contact'></input>
			</form>
            <input onClick={closeBlock} id='close-btn' value='close' type='button'></input>
		</div>
        </>
	);
};

export default FormModal;
