import React, { useEffect, useState, useRef } from "react";
import Cookies from "js-cookie";
import CreateMsgbutton from "./Createmsgbutton";
import { useForm } from "react-hook-form";

const CreateMsgForm = () => {
	const { register, handleSubmit } = useForm();

	const onSubmit = (data) => {

		var formData = new FormData()
		formData.append("nf", data )


		console.log(formData)
		fetch("http://127.0.0.1:5000/create-message", {
			method: "POST",
			body: [{"nf": "decory"}],
			mode: "cors",
			headers: {
				"Content-Type": "multipart/form-data",
				"x-access-token": Cookies.get("token"),
			},
		}).then((response) => {
			console.log(response);
		});
	};

	return (
		<>
			{/* <div  className='bg_blur'></div> */}
			<div className='form_modal_wrapper'>
				<div>
					{
						<h2 style={{ textAlign: "left", color: "#34A1AD" }}>
							{" "}
							Create New Template{" "}
						</h2>
					}
					{/*  */}

					<form name='nf' onSubmit={handleSubmit(onSubmit)}>
						{/* <input ref={register} name='File' id='file' type='file' /> */}
						<input ref={register} name='nf' type='text' />
						<input type='submit' value='submit' />
					</form>
				</div>
				{/* <input id='close-btn' value='close' type='button'></input> */}
			</div>
		</>
	);
};

export default CreateMsgForm;

