import React, { useEffect, useState, Ref, useRef } from 'react'
import Cookies from "js-cookie";


const DeleteMessageComponent = (props) =>{
    const [cancel, setCancel] = useState()
    console.log(props.cond )

    useEffect(()=>{
    setCancel(props.cond)
        // setCancel({visibility: props.cond})
    }, [props.cond])



    const cancelClick = () => {
        setCancel(!cancel)
        console.log(cancel)
    }
    const deleteFunc = () => {
        fetch(`http://localhost:5000/delete-messages/${props.selected}`,{
            method: 'DELETE',
            mode: 'cors',
            headers: {
			"Content-Type": "application/x-www-form-urlencoded",
			"Access-Control-Allow-Origin": "*",
			"x-access-token": Cookies.get("token"),
            }
        }).then((response) => response.json())
        .then((resp) => {
            window.location.reload()
        })
    }

    return(
        <>
        <div style={cancel ? {visibility: 'hidden'} : {visibility: 'visible'}} className="deleteMessageCard">
            <div className="deleteCard">
            <h1> Are you sure?</h1>
            <button onClick={cancelClick} id='submit-btn-update'>Cancel</button>
            <button onClick={deleteFunc} id='submit-btn-update'>Delete</button>
            </div>


        </div>
        </>
    )
}
export default DeleteMessageComponent;
