import React, { useEffect, useState, Ref, useRef } from 'react'
import Footer from './components/Footer'
import Cookies from "js-cookie";
import CreateMsgbutton from './components/Createmsgbutton';

const Message = () => {
	const [messageItem, setMessageItem] = useState({
		items: null,
		loaded: false,
    });
    const [query, setQuery] = useState();

	useEffect(() => {
		// setCookies(Cookies.get('token'))
		console.log(Cookies.get("token"));
		fetch("http://localhost:5000/onload-messages", {
			method: "GET",
			mode: "cors",
			headers: {
				"Content-Type": "application/x-www-form-urlencoded",
				"Access-Control-Allow-Origin": "*",
				"x-access-token": Cookies.get("token"),
			},
		})
			.then((resp) => resp.json())
			.then((items) => {
				setMessageItem({ items: Object.values(items), loaded: true });
			});
	}, []);


    var {loaded } = messageItem;

	if (!loaded) {
		return (
			<>
				<p>Loading...</p>
			</>
		);
	} else {

    return(
        <>
        <div className='app-bg'>
            {/* Card  */}
            <div className='cardHolder'>
            <div className='message_card'>
            </div>
            </div>

        <h2 style={{textAlign: 'left', marginLeft: '5%'}}>Select Template</h2>
        <ul>
            {messageItem.items.map((obj)=>(
                <li style={{color:'#6B6B6B', display: 'flex', flexDirection: 'column'}} key={obj.id}>
                    <div style={{color:'black'}}><b>{obj.title}</b></div>
                </li>
                ))}
        </ul>
        <div>
        <CreateMsgbutton />
        </div>
       <Footer />
        </div>
        </>
    )}
}

export default Message;
