import React, { useEffect } from "react";
import Cookies from "js-cookie";
import FormModal from "./CreateContact";

const Createbutton = (props) => {
    // Create Hook that opens pop-up form
    const [openModal, setOpenModal] = useEffect('none')

    const openNow = () => {
        setOpenModal('block')
    }


	return (
		<>
        <FormModal switchModal={openModal.open}/>
			<button  onClick={ openNow } className='create-btn'>Create New Contact</button>
		</>
	);
};

export default Createbutton;
