import React, { useEffect, useState, useRef } from "react";
import Cookies from "js-cookie";
import CreateMsgbutton from "./Createmsgbutton";
import { useForm } from 'react-hook-form'

const CreateMsgForm = (props) => {
    useEffect(()=>{
        setmessage(props.message)
        setImage(props.imageSet)
    }, [])


    const [message, setmessage] = useState()
    const [imageSet, setImage] = useState()
    const [render, setRender] = useState(false)
    const mForm = useRef(null)

    const { register, handleSubmit } = useForm()

    const onSubmit = (data)=>{
        console.log(message)
            fetch('http://localhost:5000/create-contact',{
            method: 'POST',
            body: message,
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*',
                "x-access-token": Cookies.get("token"),
            }
        }).then(res => res.json())
        .then(json => {
            if (json.status === 200){
                window.location = 'http://localhost:3000/messages'
            } else{

                setRender(!render)
            }
        })

    }




	return (
        <>
        {/* <div  className='bg_blur'></div> */}
		<div  className='form_modal_wrapper'>
            <div>
            {<h2 style={{textAlign: 'left', color: '#34A1AD'}}> Create New Template </h2>}
			<form onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data" id='form-data'>
				<label id='email'>Title</label>
				<input
                ref={register}
					className='cus-input'
					type='text'
					name='title'
					placeholder='Name'
					onChange={(e) =>
						setmessage({ ...message, title: e.target.value })}>
				</input>
				<label id='email'>Custom SMS Message</label>
				<textarea
                ref={register}
                    className='messagebox'
                    placeholder='Write your custom sms messages here...'
					name='message'
                    rows= '5'
                    cols='40'

					onChange={(e) =>
						setmessage({ ...message, message: e.target.value })}
				></textarea>
                <label id='email'>Url</label>
                <input
                ref={register}
					className='cus-input'
					type='text'
					name='url'
					placeholder='www.yoururl.com/here'
					onChange={(e) =>
						setmessage({ ...message, url: e.target.value })}>
				</input>
                <input ref={register} name='image' id='file' type='file' onChange={(e) => setmessage({...message, image: e.target.files[0]})}/>
				<input id='submit-btn' type='submit' value='Create Contact'></input>
			</form>
            </div>
            {/* <input id='close-btn' value='close' type='button'></input> */}
		</div>
        </>
	);
};

export default CreateMsgForm;
